html:
{#
    Print Format: PO Nirmaan (Revised)
    DocType: Procurement Order
#}

{# --- Company Details (Customized) --- #}
{%- set company_details = {
    "name": "Nirmaan (Stratos Infra Technologies Pvt. Ltd.)",
    "logo_url": "https://s3.ap-south-1.amazonaws.com/nirmaan-stack-test-bucket/test/2025/05/19/File/Z3C3GD6J_logo-svg.svg",
    "gst_numbers": {
        "29ABFCS9095N1Z9": "Bengaluru",
        "06ABFCS9095N1ZH": "Gurugram"
    },
    "addresses": {
        "29ABFCS9095N1Z9": "1st Floor, 234, 9th Main, 16th Cross, Sector 6, HSR Layout, Bengaluru - 560102, Karnataka",
        "06ABFCS9095N1ZH": "7th Floor, MR1, ALTF Global Business Park Cowarking Space, Mehrauli Gurugram Rd, Tower D, Sikanderpur, Gurugram, Haryana - 122002"
    },
    "default_address": "Company address details missing or project GST not set.",
    "phone_no": "YOUR_COMPANY_PHONE",
    "email": "YOUR_COMPANY_EMAIL",
    "website": "YOUR_COMPANY_WEBSITE"
} -%}

{# Determine current company address and GST based on PO's project_gst #}
{%- set current_company_gst = doc.project_gst if doc.project_gst in company_details.gst_numbers else None -%}
{%- set current_company_address = company_details.addresses[current_company_gst] if current_company_gst else company_details.default_address -%}
{%- set currency_symbol = "₹" -%}
{# {%- set company_currency = frappe.db.get_default("currency") or "INR" -%} #}
{# {%- set currency_symbol  = frappe.utils.get_currency_symbol(company_currency) -%} #}

{# ----------- 1. Macro – Address formatter --------------------- #}
{% macro format_address(addr_name) -%}
    {%- if not addr_name -%}N/A
    {%- else -%}
        {%- set ad = frappe.get_doc("Address", addr_name) -%}
        {%- set parts = [] -%}
        {%- if ad.address_line1        %}{% set parts = parts + [ad.address_line1]        %}{% endif -%}
        {%- if ad.address_line2        %}{% set parts = parts + [ad.address_line2]        %}{% endif -%}
        {%- set csp = [] -%}
        {%- if ad.city                 %}{% set csp = csp + [ad.city]                     %}{% endif -%}
        {%- if ad.state                %}{% set csp = csp + [ad.state]                    %}{% endif -%}
        {%- if ad.pincode              %}{% set csp = csp + [ad.pincode]                  %}{% endif -%}
        {%- if csp                     %}{% set parts = parts + [csp | join(" ")]         %}{% endif -%}
        {%- if ad.country              %}{% set parts = parts + [ad.country]              %}{% endif -%}
        {{ parts | join("<br>") | safe }}
    {%- endif -%}
{%- endmacro %}

{%- set project_doc = frappe.get_doc("Projects", doc.project) if doc.project else None -%}
{%- set vendor_doc = frappe.get_doc("Vendors", doc.vendor) if doc.vendor else None -%}

{# ----------- 3. Pre-rendered HTML blocks ----------------------- #}
{% set vendor_address_html  = format_address(vendor_doc.vendor_address)
                                  if vendor_doc else "N/A" %}
{% set project_address_html = format_address(project_doc.project_address)
                                  if project_doc else "N/A" %}

{# Helper to parse the first value from the advance string #}
{%- macro get_first_advance_value(advance_string) -%}
    {%- if advance_string and advance_string is string -%}
        {%- set parts = advance_string.split(',') -%}
        {%- if parts and parts[0] -%}
            {{- (parts[0] | trim | float) if parts[0] | trim else 0 -}}
        {%- else -%}
            0
        {%- endif -%}
    {%- else -%}
        {{- (advance_string | float) if advance_string is number else 0 -}} {# Handle if it's already a number or None #}
    {%- endif -%}
{%- endmacro -%}
{%- set advance_paid_value = get_first_advance_value(doc.advance) -%}


<style>
    body { font-family: "Helvetica Neue", Helvetica, Arial, sans-serif; font-size: 10px; color: #333; margin: 0; padding: 0; }
    /*.invoice-container { width: 100%; max-width: 800px; margin: 0 auto; padding: 0 15px 15px 15px; }*/

    @page {
        size: A4;
        margin: 45mm 12mm 20mm 12mm;   /* top, right, bottom, left */
    }

    .header img.logo { max-width: 140px; max-height: 40px; margin-bottom: 8px; float: left; }
    .company-info-header { float: right; text-align: right; }
    .company-info-header p { margin: 0 0 2px 0; font-size: 9.5px; }
    .document-title {
        font-size: 20px; font-weight: bold; color: #1a1a1a; text-transform: uppercase;
        margin-bottom: 15px; text-align: center; clear: both; padding-top: 10px;
        border-bottom: 2px solid #333; padding-bottom: 10px;
    }
    .po-details { margin-bottom: 15px; overflow: hidden; }
    .po-details .left, .po-details .right { width: 49%; float: left; font-size: 9.5px; }
    .po-details .right { float: right; text-align: right; }
    .po-details p, .address-section p { margin: 0 0 3px 0; line-height: 1.4; }
    .address-section { margin-bottom: 15px; overflow: hidden; }
    .address-section .billed-to, .address-section .shipped-to {
        width: 49%; float: left; min-height: 80px; font-size: 9.5px;
    }
    .address-section .shipped-to { float: right; }
    .address-section h4 {
        font-size: 10px; font-weight: bold; text-transform: uppercase; color: #555;
        margin-top: 0; margin-bottom: 6px;
    }
    .address-block       { margin-bottom:15px; overflow:hidden; }
    .address-block__col  { width:48%; float:left; font-size:9.5px; }
    .address-block__col + .address-block__col { float:right; }
    .items-table { width: 100%; border-collapse: collapse; margin-bottom: 15px; }
    .items-table th, .items-table td { border: 1px solid #ddd; padding: 5px 7px; text-align: left; }
    .items-table th { background-color: #f0f0f0; font-weight: bold; font-size: 9.5px; text-transform: uppercase; }
    .items-table td { font-size: 9.5px; }
    .items-table .text-right { text-align: right; }
    .items-table .text-center { text-align: center; }
    .totals-section { width: 100%; margin-top: 15px; overflow: hidden; }
    .totals-section .notes-terms { width: 58%; float: left; font-size: 9.5px; }
    .totals-section .summary { width: 40%; float: right; }
    .totals-section .summary table { width: 100%; font-size: 9.5px; }
    .totals-section .summary td { padding: 3px 0; }
    .totals-section .summary td:first-child { font-weight: bold; color: #444; }
    .totals-section .summary .grand-total td {
        font-weight: bold; font-size: 11px; border-top: 1px solid #333; border-bottom: 1px solid #333;
        padding-top: 6px; padding-bottom: 6px;
    }
    .footer-notes {
        margin-top: 25px; font-size: 8.5px; color: #777; border-top: 1px dashed #ccc;
        padding-top: 8px; text-align: center;
    }
    .signature-area { margin-top: 40px; text-align: right; }
    .signature-area .line { width: 180px; border-top: 1px solid #333; margin-left: auto; margin-bottom: 5px; }
    .signature-area p { font-size: 9.5px; margin:0; }
</style>

{# -----------------  HEADER – will repeat on every page  ----------------- #}
<div id="header-html" style="display: none">       {# must be OUTSIDE .print-format #}
  <table style="width:100%; font-family:Helvetica,Arial,sans-serif; font-size:9.5px">
    <tr>
      <td style="width:40%">
        <img src="{{ company_details.logo_url }}" style="max-width:140px; max-height:38px">
      </td>
      <td style="text-align:right; font-size:11px">
        <strong>{{ company_details.name }}</strong><br>
        {{ current_company_address }}<br>
        {% if current_company_gst %}
          <strong>GSTIN:</strong> {{ current_company_gst }}
        {% endif %}
      </td>
    </tr>
  </table>
</div>

{# -------------  FOOTER (optional) – also repeats on every page --------- #}
<div id="footer-html" style="display:none">
  <div style="width:100%; text-align:center; font-size:8.5px;">
    Page <span class="page"></span> of <span class="topage"></span>
  </div>
</div>

<div class="print-format">

<!--<div class="invoice-container">-->
    <div class="document-title">Purchase Order</div>
    <!--<h2 style="text-align:center; margin:0 0 12px 0;">Purchase Order</h2>-->
    <div class="po-details">
        <div class="left">
            <p><strong>PO Number:</strong> {{ doc.name }}</p>
            <p><strong>PO Date:</strong> {{ frappe.utils.formatdate(doc.creation, "dd-MM-yyyy") }}</p>
            {% if doc.procurement_request %}
            <p><strong>PR Reference:</strong> {{ doc.procurement_request }}</p>
            {% endif %}
        </div>
        <div class="right">
            <p><strong>Status:</strong> <span style="font-weight:bold; color: #007bff;">{{ doc.status }}</span></p>
        </div>
    </div>
    <div style="clear:both;"></div>

    {# In the address display section: #}
    <div class="address-block">
      <div class="address-block__col">
          <h4>VENDOR DETAILS</h4>
          <p><strong>{{ vendor_doc.vendor_name if vendor_doc else doc.vendor }}</strong></p>
          {{ vendor_address_html }}
          {% if vendor_doc and vendor_doc.vendor_gst %}
            <p><strong>GSTIN:</strong> {{ vendor_doc.vendor_gst }}</p>
          {% endif %}
      </div>

      <div class="address-block__col">
          <h4>SHIP-TO / PROJECT DETAILS</h4>
          <p><strong>{{ project_doc.project_name if project_doc else doc.project }}</strong></p>
          {{ project_address_html }}
          {% if doc.delivery_contact %}
            <p><strong>Site Contact:</strong> {{ doc.delivery_contact }}</p>
          {% endif %}
      </div>
  </div>
<div style="clear:both;"></div>

<table class="items-table">
    <thead>
        <tr>
            <th style="width:5%;">Sr.</th>
            <th style="width:35%;">Item Description</th>
            <th style="width:10%;" class="text-center">Make</th>
            <th style="width:8%;" class="text-center">Qty</th>
            <th style="width:8%;" class="text-center">Unit</th>
            <th style="width:12%;" class="text-right">Rate ({{ currency_symbol }})</th>
            <th style="width:10%;" class="text-right">Tax %</th>
            <th style="width:12%;" class="text-right">Amount ({{ currency_symbol }})</th>
        </tr>
    </thead>
    <tbody>
        {% set total_qty = namespace(value=0.0) %}
        {% set subtotal = namespace(value=0.0) %}
        {% set total_tax_amount_on_items = namespace(value=0.0) %}

        {# Initialize items_to_loop variable #}
        {% set items_to_loop = [] %}

        {# Attempt to parse if doc.order_list is a string #}
        {% if doc.order_list and doc.order_list is string %}
            {% set parsed_data = json.loads(doc.order_list) %}
            {% if parsed_data is mapping and parsed_data.list is defined and parsed_data.list is iterable %}
                {% set items_to_loop = parsed_data.list %}
            {% endif %}
        {# Else, if it's already a dictionary with a list #}
        {% elif doc.order_list is mapping and doc.order_list.list is defined and doc.order_list.list is iterable %}
            {% set items_to_loop = doc.order_list.list %}
        {% endif %}

        {% if items_to_loop | length > 0 %}
            {% for item in items_to_loop %}
                {% if item is mapping %}
                    <tr>
                        <td class="text-center">{{ loop.index }}</td>
                        <td>
                            {{ item.get("item", "N/A") }}
                            {% if item.get("comment") %}<br><small><em>Note: {{ item.get("comment") }}</em></small>{% endif %}
                        </td>
                        <td class="text-center">{{ item.get("make", "-") }}</td>
                        {%- set current_item_quantity = (item.get("quantity") | float(0)) -%}
                        <td class="text-center">{{ current_item_quantity | round(2) }}</td>
                        {%- set total_qty.value = total_qty.value + current_item_quantity -%}
                        <td class="text-center">{{ item.get("unit", "N/A") }}</td>
                        {%- set current_item_quote = (item.get("quote") | float(0)) -%}
                        <td class="text-right">{{ "%.2f" | format(current_item_quote) }}</td>
                        {%- set current_item_tax_percent = (item.get("tax") | float(0)) if item.get("tax") is not none else 0.0 -%}
                        <td class="text-right">{{ current_item_tax_percent }}%</td>

                        {%- set item_base_amount = current_item_quantity * current_item_quote -%}
                        {%- set current_item_tax_amount = item_base_amount * current_item_tax_percent / 100.0 -%}
                        {%- set item_total_amount = item_base_amount + current_item_tax_amount -%}
                        <td class="text-right">{{ "%.2f" | format(item_total_amount | float ) }}</td>

                        {%- set subtotal.value = subtotal.value + item_base_amount -%}
                        {%- set total_tax_amount_on_items.value = total_tax_amount_on_items.value + current_item_tax_amount -%}
                    </tr>
                {% else %}
                    <tr><td colspan="8" style="color:red; text-align:center;">Malformed item data found in order_list (item not a dict)</td></tr>
                {% endif %}
            {% endfor %}
        {% else %}
            <tr><td colspan="8" style="text-align:center; padding: 20px;">No items found in this order or order_list is invalid.</td></tr>
        {% endif %}
    </tbody>
</table>

    <div class="totals-section">
        <div class="notes-terms">
            <p><strong>Total Quantity:</strong> {{ (total_qty.value | float) | round(2) }}</p>
            <br>
            {# Corrected all values in final_grand_total calculation to use | float #}
            {% set final_grand_total = (subtotal.value | float) + (total_tax_amount_on_items.value | float) + ((doc.loading_charges or 0) | float) + ((doc.freight_charges or 0) | float) - (advance_paid_value | float(0)) %}
            <p><strong>Amount in Words:</strong><br>
                {{ frappe.utils.money_in_words(final_grand_total, "INR") }} {# Assuming INR or use system default #}
            </p>
            {% if doc.notes %}
            <div style="margin-top:10px;">
                <h4 style="font-size:10px; font-weight:bold; margin-bottom:3px;">NOTES & TERMS:</h4>
                <div style="font-size:9px; white-space: pre-wrap;">{{ doc.notes }}</div>
            </div>
            {% endif %}
        </div>

        <div class="summary">
            <table>
                <tr>
                    <td>Subtotal:</td>
                    <td class="text-right">{{ currency_symbol }} {{ "%.2f" | format(subtotal.value | float) }}</td>
                </tr>
                <tr>
                    <td>Total Item Tax:</td>
                    <td class="text-right">{{ currency_symbol }} {{ "%.2f" | format(total_tax_amount_on_items.value | float) }}</td>
                </tr>
                {% if doc.loading_charges and (doc.loading_charges | string | trim != "") and ((doc.loading_charges or 0) | float) > 0 %}
                <tr>
                    <td>Loading Charges:</td>
                    <td class="text-right">{{ currency_symbol }} {{ "%.2f" | format((doc.loading_charges or 0) | float) }}</td>
                </tr>
                {% endif %}
                {% if doc.freight_charges and (doc.freight_charges | string | trim != "") and ((doc.freight_charges or 0) | float) > 0 %}
                <tr>
                    <td>Freight Charges:</td>
                    <td class="text-right">{{ currency_symbol }} {{ "%.2f" | format((doc.freight_charges or 0) | float) }}</td>
                </tr>
                {% endif %}
                {% if (advance_paid_value | float(0)) > 0 %}
                <tr>
                    <td>Advance Paid:</td>
                    <td class="text-right" style="color:green;">- {{ currency_symbol }} {{ "%.2f" | format(advance_paid_value | float) }}</td>
                </tr>
                {% endif %}
                <tr class="grand-total">
                    <td>GRAND TOTAL:</td>
                    <td class="text-right">{{ currency_symbol }} {{ "%.2f" | format(final_grand_total | float) }}</td>
                </tr>
            </table>
        </div>
    </div>
    <div style="clear:both;"></div>

    <div class="footer-notes">
        This is a computer-generated Purchase Order.
        <p>For detailed terms and conditions, please visit our website or refer to the master agreement.</p>
    </div>

    <div class="signature-area">
        <div class="line"></div>
        <p>For <strong>{{ company_details.name }}</strong></p>
        <p>(Authorized Signatory)</p>
    </div>
</div>
<!--</div>-->

{#
    Print Format : SR Tax Invoice – single-line grid, no background
    DocType      : Service Requests
#}

{# ──────────────────────────────────────────────────────────────
   0 . Company / project / customer / vendor variables
   ────────────────────────────────────────────────────────────── #}
{%- set nirmaan_company_details = {
    "name"      : "Nirmaan (Stratos Infra Technologies Pvt. Ltd.)",
    "gst_numbers": {
        "29ABFCS9095N1Z9": {"name": "Karnataka", "code": "29"},
        "06ABFCS9095N1ZH": {"name": "Haryana",   "code": "06"},
        "09ABFCS9095N1ZB": {"name": "Noida", "code":"09"}
    },
    "addresses": {
        "29ABFCS9095N1Z9": "1st Floor, 234, 9th Main, 16th Cross, Sector 6, HSR Layout, Bengaluru 560102",
        "06ABFCS9095N1ZH": "7th Floor, MR1, ALTF Global Business Park, Mehrauli–Gurugram Rd, Tower D, Gurugram 122002",
        "09ABFCS9095N1ZB": "MR1, Plot no. 21 & 21A, AltF 142 Noida, Sector 142, Noida - 201305, Uttar Pradesh"
    },
    "default_address": "Nirmaan address not set"
} -%}

{%- set n_key         = doc.project_gst if doc.project_gst in nirmaan_company_details.gst_numbers else None -%}
{%- set n_addr        = nirmaan_company_details.addresses.get(n_key, nirmaan_company_details.default_address) -%}
{%- set n_gstin       = n_key -%}
{%- set n_state_name  = nirmaan_company_details.gst_numbers[n_key].name if n_key else "N/A" -%}
{%- set n_state_code  = nirmaan_company_details.gst_numbers[n_key].code if n_key else "N/A" -%}

{%- set project_doc   = frappe.get_doc("Projects",  doc.project)  if doc.project else None -%}
{%- set vendor_doc    = frappe.get_doc("Vendors", doc.vendor) if doc.vendor else None -%}
{%- set customer_doc  = frappe.get_doc("Customers", project_doc.customer) if project_doc and project_doc.customer else None -%}

{%- set is_gst_applicable = doc.gst == "true" -%}
{%- set gst_rate = 18.0 -%}

{% macro format_address(addr) -%}
    {%- if not addr -%}N/A{%- else -%}
        {%- set a = frappe.get_doc("Address", addr) -%}
        {%- set p = [] -%}
        {%- if a.address_line1 %}{% set p = p + [a.address_line1] %}{% endif -%}
        {%- if a.address_line2 %}{% set p = p + [a.address_line2] %}{% endif -%}
        {%- set csp = [] -%}
        {%- if a.city    %}{% set csp = csp + [a.city]    %}{% endif -%}
        {%- if a.state   %}{% set csp = csp + [a.state]   %}{% endif -%}
        {%- if a.pincode %}{% set csp = csp + [a.pincode] %}{% endif -%}
        {%- if csp %}{% set p = p + [csp | join(" ")] %}{% endif -%}
        {%- if a.country %}{% set p = p + [a.country] %}{% endif -%}
        {{ p | join("<br>") | safe }}
    {%- endif -%}
{%- endmacro %}

{%- set project_addr_html = format_address(project_doc.project_address) if project_doc and project_doc.project_address else "N/A" -%}
{%- set vendor_addr_html = format_address(vendor_doc.vendor_address) if vendor_doc and vendor_doc.vendor_address else "N/A" -%}
{%- set vendor_gstin = vendor_doc.vendor_gst if vendor_doc else "N/A" -%}
{%- set vendor_state_name = frappe.get_doc("Address", vendor_doc.vendor_address).state if vendor_doc and vendor_doc.vendor_address else "N/A" -%}
{%- set vendor_state_code = vendor_gstin[0:2] if vendor_gstin and vendor_gstin != "N/A" else "N/A" -%}
{%- set currency_symbol= "₹" -%}

{# ──────────────────────────────────────────────────────────────
   1 . Styles (unified 1-px grid, no colour fills)
   ────────────────────────────────────────────────────────────── #}
<style>
    body      { font-family:"Roboto",Arial,sans-serif; font-size:8.5pt; margin:0; color:#000; }
    table     { width:100%; border-collapse:collapse; }
    tr        { page-break-inside:avoid; }
    td,th     { border:1px solid #555; padding:2px 3px; vertical-align:top; font-size:8.5pt; }
    th        { font-weight:bold; text-align:center; }

    .w-50     { width:50%; }

    /* outer grids */
    .section-table td { padding:2px 3px; }

    /* nested info-table : inner lines only */
    /* .info-table        { width:100%; border-collapse:collapse; }
    .info-table td     { border:1px solid #555; padding:1.5px 3px; }
    .info-table        { border:none; }
    .info-table tr:first-child  td { border-top:0; }
    .info-table tr:last-child   td { border-bottom:0; }
    .info-table tr td:first-child{ border-left:0; }
    .info-table tr td:last-child { border-right:0; } */

    .info-table td { border:1px solid #555; padding:1.5px 3px; font-size:7.5pt; vertical-align: top;}
    .info-table { border:none; } /* Remove outer border of the nested table */
    .info-table tr:first-child td  { border-top:0; }
    .info-table tr:last-child td   { border-bottom:0; }
    .info-table tr td:first-child  { border-left:0; }
    .info-table tr td:last-child   { border-right:0; }

    /* .label-cell { width:50%; font-weight:normal; border-bottom:0; padding-bottom:0; }
    .value-cell { width:50%; font-weight:bold;   border-top:0;   padding-top:.5px; }
    .terms-cell { border-top:1px solid #555; } */

    .label-cell { width:50%; font-weight:normal; border-bottom:0; padding-bottom:0; text-align:left; }
    .value-cell { width:50%; font-weight:bold;   border-top:0;   padding-top:.5px; text-align:left; min-height: 10px; word-wrap: break-word; }

    .text-center{ text-align:center; }
    .text-right { text-align:right;  }
    .font-bold  { font-weight:bold;  }
    .font-small { font-size: 7pt; } /* For labels like "Buyer (Bill to)" */

    @page { size:A4; margin:30mm 8mm 18mm 8mm; }

    #header-html h2 { margin:0 0 12px 0; text-align:center; }
    #footer-html    { font-size:7.5pt; text-align:center; font-family:"Roboto",Arial,sans-serif; }
</style>

{# ──────────────────────────────────────────────────────────────
   2 . Header / Footer
   ────────────────────────────────────────────────────────────── #}
<div id="header-html" style="display:none;">
    <h2>Tax Invoice</h2>
</div>

<div id="footer-html" style="display:none;">
    This is a Computer Generated Invoice  |  Page <span class="page"></span> of <span class="topage"></span>
</div>

{# ──────────────────────────────────────────────────────────────
   3 . Main document
   ────────────────────────────────────────────────────────────── #}
<div class="print-format">

<!-- SECTION 1 : Vendor vs Invoice quick info -->
<table class="section-table">
  <tr>
    <td class="w-50">
        <p class="font-bold" style="font-size:9.5pt;">
            {{ vendor_doc.vendor_name if vendor_doc else "Vendor N/A" }}
        </p>
        {{ vendor_addr_html }}<br>
        <!-- {% if vendor_gstin and vendor_gstin != "N/A" %}
