 <!--SECTION 3 : Item grid -->
<table style="margin-top:3px;">
   <thead>
     <tr>
       <th style="width:5%;">Sl&nbsp;No.</th>
       <th style="width:{% if is_gst_applicable %}48%{% else %}58%{% endif %};">Particulars</th>
       <th style="width:8%;" class="text-center">Quantity</th>
       <th style="width:8%;" class="text-center">Rate&nbsp;per</th>
       {% if is_gst_applicable %}
       <th style="width:8%;" class="text-center">GST</th>
       {% endif %}
       <th style="width:{% if is_gst_applicable %}23%{% else %}21%{% endif %};" class="text-right">Amount</th>
     </tr>
   </thead>
   <tbody>
     {% set subtotal = namespace(value=0.0) %}
     {% set total_tax = namespace(value=0.0) %}
     {% set grand_total = namespace(value=0.0) %}

     {# build items array the same way you did earlier #}
     {% set items = [] %}
     {% if doc.service_order_list and doc.service_order_list is string %}
         {% set parsed = json.loads(doc.service_order_list) %}
         {% if parsed is mapping and parsed.list is defined %}{% set items = parsed.list %}{% endif %}
     {% elif doc.service_order_list is mapping and doc.service_order_list.list is defined %}
         {% set items = doc.service_order_list.list %}
     {% endif %}

     {% if items|length %}
       {% for it in items %}
         {% set qty   = (it.quantity  or 0)|float %}
         {% set rate  = (it.rate      or 0)|float %}
         {% set amt   = qty * rate %}
         {% set subtotal.value = subtotal.value + amt %}

         {% if is_gst_applicable %}
             {% set tax_amount = amt * (gst_rate / 100) %}
             {% set total_tax.value = total_tax.value + tax_amount %}
             {% set item_total = amt + tax_amount %}
         {% else %}
             {% set item_total = amt %}
         {% endif %}

         {% set grand_total.value = grand_total.value + item_total %}
         <tr>
           <td class="text-center">{{ loop.index }}</td>
           <td>{{ it.description }}</td>
           <td class="text-center">{{ qty if qty else "-" }} {{ it.uom if qty else "" }}</td>
           <td class="text-right">{{ rate and ('%.2f'|format(rate)) or '-' }}</td>
           {% if is_gst_applicable %}
           <td class="text-center">{{ gst_rate }}%</td>
           {% endif %}
           <td class="text-right">{{ currency_symbol }} {{ '%.2f'|format(amt) }}</td>
         </tr>
       {% endfor %}
     {% else %}
       <tr><td colspan="{{ 6 if is_gst_applicable else 5 }}" class="text-center" style="padding:12px;">No service items found</td></tr>
     {% endif %}

     <tr>
        <td colspan="{% if is_gst_applicable %}4{% else %}3{% endif %}" class="font-bold text-right">Total</td>
        <td {% if is_gst_applicable %}{% else %}colspan="1"{% endif %}></td>
        <td class="font-bold text-right">{{ currency_symbol }} {{ "%.2f"|format(subtotal.value) }}</td>
     </tr>

     {% if is_gst_applicable %}
        <tr>
            <td colspan="4" class="font-bold text-right">Add: GST @ {{ gst_rate }}%</td>
            <td></td>
            <td class="font-bold text-right">{{ currency_symbol }} {{ "%.2f"|format(total_tax.value) }}</td>
        </tr>
     {% endif %}

     <tr>
        <td colspan="{% if is_gst_applicable %}4{% else %}3{% endif %}" class="font-bold text-right">Grand Total</td>
        <td {% if is_gst_applicable %}{% else %}colspan="1"{% endif %}></td>
        <td class="font-bold text-right">{{ currency_symbol }} {{ "%.2f"|format(grand_total.value) }}</td>
     </tr>
   </tbody>
</table>

<!-- SECTION 4 : Amount in words / taxable summary -->
<table style="margin-top:3px;">
  <tr>
    <td style="width:70%; border-right:0;">
        Amount Chargeable (in words)<br>
