{%- set nirmaan_gst_map = {
    "29ABFCS9095N1Z9": {"state": "Karnataka", "code": "29", "address": "First floor, 234, 9th Main, 16th Cross Rd, Sector 6, HSR Layout, Bengaluru, Karnataka 560102"},
    "06ABFCS9095N1ZH": {"state": "Haryana",   "code": "06", "address": "7th Floor, MR1, ALTF Global Business Park, Mehrauli–Gurugram Rd, Tower D, Gurugram 122002"},
    "09ABFCS9095N1ZB": {"state": "Uttar Pradesh", "code":"09", "address": "MR1, Plot no. 21 & 21A, AltF 142 Noida, Sector 142, Noida - 201305"}
} -%}

{%- set gst_key = project_doc.project_gst if project_doc and project_doc.project_gst in nirmaan_gst_map else "29ABFCS9095N1Z9" -%}
{%- set company_info = nirmaan_gst_map[gst_key] -%}

{# --------------------------------------------------------------------------------- #}
<style>
    /* GLOBAL RESET & FONTS */
    body {
        font-family: "Inter", -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
        font-size: 12px;
        color: #1f2937;
        line-height: 1.5;
        margin: 0;
        padding: 0;
    }

    /* PRINT RESET */
    @media print {
        @page {
            size: A4;
            margin: 10mm;
        }
        .page-break { page-break-after: always; }
        .page-break-after { page-break-after: always; }
        .page-break-before { page-break-before: always; }
        .avoid-break { page-break-inside: avoid !important; }

        /* Force background colors */
        * {
            -webkit-print-color-adjust: exact !important;
            print-color-adjust: exact !important;
        }
    }

    /* UTILITIES */
    .w-100 { width: 100%; }
    .w-20 { width: 20%; }
    .w-30 { width: 30%; }
    .w-40 { width: 40%; }
    .w-50 { width: 50%; }

    .text-center { text-align: center; }
    .text-right { text-align: right; }
    .text-left { text-align: left; }

    .valign-top { vertical-align: top; }
    .valign-middle { vertical-align: middle; }

    .mt-4 { margin-top: 4px; }
    .mt-10 { margin-top: 10px; }
    .mt-15 { margin-top: 15px; }
    .mt-20 { margin-top: 20px; }
    .mb-2 { margin-bottom: 2px; }
    .mb-4 { margin-bottom: 4px; }
    .mb-10 { margin-bottom: 10px; }
    .mb-15 { margin-bottom: 15px; }
    .mb-20 { margin-bottom: 20px; }

    .p-4 { padding: 4px; }
    .p-10 { padding: 10px; }
    .pl-20 { padding-left: 20px; }
    .pr-10 { padding-right: 10px; }
    .pr-15 { padding-right: 15px; }

    .font-bold { font-weight: 700; }
    .font-medium { font-weight: 500; }
    .font-normal { font-weight: 400; }

    .text-sm { font-size: 11px; }
    .text-xs { font-size: 10px; }
    .text-md { font-size: 13px; }
    .text-lg { font-size: 14px; }
    .text-xl { font-size: 16px; }

    /* COLORS */
    .text-red { color: #dc2626; }
    .text-orange { color: #d97706; }
    .text-green { color: #059669; }
    .text-gray { color: #6b7280; }
    .text-black { color: #000; }
    .text-white { color: #fff; }

    .bg-green-light { background-color: #d1fae5; color: #065f46; }
    .bg-yellow-light { background-color: #fffbeb; color: #d97706; }
    .bg-red-light { background-color: #fef2f2; color: #dc2626; }
    .bg-gray-light { background-color: #f3f4f6; color: #4b5563; }
    .bg-yellow-vlight { background-color: #fef9c3; }

    /* COMPONENTS */

    /* Header */
    .header-table td {
        border-bottom: 1px solid #9ca3af;
        padding-bottom: 15px;
        vertical-align: top;
    }
    .logo-cell { width: 60px; padding-right: 15px; }
    .logo-img { max-width: 150px; width: auto; height: 40px; object-fit: contain; }

    /* Info Table */
    .info-table { width: 100%; border-collapse: collapse; margin-bottom: 10px; }
    .info-table td { padding: 4px 10px 4px 0; }

    /* Section Title */
    .section-title {
        font-size: 13px;
        font-weight: 700;
        color: #374151;
        margin-bottom: 12px;
        text-transform: uppercase;
        border-bottom: 1px solid #e5e7eb;
        padding-bottom: 4px;
        display: block;
        width: 100%;
    }

    /* Manpower */
    .manpower-table { width: 100%; border-collapse: separate; border-spacing: 0 5px; }
    .manpower-row td {
        background-color: #f9fafb;
        border: 1px solid #e5e7eb;
        padding: 8px 15px;
        vertical-align: middle;
    }
    .manpower-label { border-right: none; border-radius: 6px 0 0 6px; }
    .manpower-count { border-left: none; border-radius: 0 6px 6px 0; text-align: right; font-weight: 700; }

    .remarks-box {
        background-color: #f9fafb;
        padding: 10px;
        border-radius: 6px;
        font-size: 11px;
        border: 1px solid #e5e7eb;
        margin-top: 10px;
    }

    /* Work Plan */
    .work-plan-ul { margin: 0; padding-left: 20px; color: #111; }
    .work-plan-li { 
        margin-bottom: 8px; 
        font-size: 12px;
        white-space: pre-wrap; /* Crucial for multi-line support */
    }

    /* Data Table (Progress) */
    .data-table {
        width: 100%;
        border-collapse: collapse;
    }
    .data-table th {
        background-color: #f9fafb;
        border: 1px solid #e5e7eb;
        padding: 10px;
        font-size: 11px;
        font-weight: 800;
        text-transform: uppercase;
        color: #111;
        text-align: center;
    }
    .data-table td {
        border: 1px solid #e5e7eb;
        padding: 12px;
        vertical-align: middle;
    }

    .badge {
        display: inline-block;
        padding: 4px 10px;
        border-radius: 4px; 
        font-size: 10px;
        font-weight: 600;
        min-width: 60px;
        text-align: center;
        white-space: nowrap;
    }

    .remarks-bubble {
        margin-top: 4px; 
        padding: 4px; 
        font-size: 10px; 
        text-align: left;
    }

    .group-header-box {
        background-color: #f9fafb;
        border-radius: 6px;
        border-left: 4px solid #dc2626;
        padding: 10px;
        margin-bottom: 10px;
    }

    /* Progress Circle Components */
    .progress-circle-group td { border: none; }
    .progress-pct-text {
        text-align: center; 
        margin-top: -24px; 
        font-size: 9px; 
        font-weight: bold; 
        color: #dc2626; 
        position: relative; 
        z-index: 10; 
        line-height: 1;
    }

    /* Images */
    .image-table { width: 100%; border-collapse: separate; border-spacing: 10px; }
    .image-cell { width: 50%; border: 1px solid #e5e7eb; background: #fff; vertical-align: top; padding: 0; }
    .image-container { height: 250px; width: 100%; background-color: #f3f4f6; text-align: center; overflow: hidden; line-height: 250px; }
    .image-actual { max-width: 100%; max-height: 100%; vertical-align: middle; width: auto; height: auto; }
    .image-meta { padding: 10px; }

    .image-location { margin-bottom: 5px; font-weight: 500; line-height: 1.2; }
    .image-remarks { padding: 5px; font-size: 10px; color: #854d0e; line-height: 1.2; }

</style>

{# MACRO: Header Block #}
{% macro header_block() %}
<table class="header-table w-100">
    <tr>
        <td class="logo-cell">
            <img src="https://stack.nirmaan.app/assets/nirmaan_stack/frontend/assets/logo-svg-BptBZTzQ.svg" class="logo-img" alt="Logo">
        </td>
        <td>
            <div class="text-xl font-bold text-black">Nirmaan(Stratos Infra Technologies Pvt. Ltd.)</div>
            <div class="text-sm text-gray mt-4">{{ company_info.address }}</div>
        </td>
    </tr>
</table>
{% endmacro %}

<div class="main-container">

    {# ==================== PAGE 1: PROJECT INFO + MANPOWER ==================== #}
    <div class="page-break-after">
        {{ header_block() }}

        <div class="mt-20"></div>

        {# PROJECT INFO #}
        {% set zone_val = doc.report_zone or "" %}
        {% if zone_val %}{% set zone_val = " (" + zone_val + ")" %}{% endif %}

        <table class="info-table">
            <tr>
                <td class="w-20 font-bold text-black">Project + Zone:</td>
                <td class="w-30 font-normal">{{ project_doc.project_name }}{{ zone_val }}</td>
                <td class="w-20 font-bold text-black text-center">Start Date:</td>
                <td class="w-30 font-normal text-right">{{ frappe.utils.formatdate(project_doc.project_start_date, "dd-MMM-yyyy") if project_doc.project_start_date else "--" }}</td>
            </tr>
            <tr>
                <td class="font-bold text-black">Project Manager:</td>
                <td class="font-normal">{{ owner_doc.full_name if owner_doc else doc.owner }}</td>
                <td class="font-bold text-black text-center">Report Date:</td>
                <td class="font-normal text-right">{{ frappe.utils.formatdate(doc.report_date, "dd-MMM-yyyy") }}</td>
            </tr>
        </table>

        <div class="mt-20"></div>

        {# MANPOWER #}
        {% set active_manpower = [] %}
        {% for mp in doc.manpower %}
            {% if (mp.count | float) > 0 %}
                {% set _ = active_manpower.append(mp) %}
            {% endif %}
        {% endfor %}

        {% if active_manpower | length > 0 %}
            <div class="section-title">MANPOWER DETAILS</div>
            <table class="manpower-table">
                {% for mp in active_manpower %}
                <tr class="manpower-row">
                    <td class="manpower-label">{{ mp.label }}</td>
                    <td class="manpower-count">{{ mp.count }}</td>
                </tr>
                {% endfor %}
            </table>

            {% if doc.manpower_remarks %}
            <div class="remarks-box">
                <span class="font-bold text-gray mb-2" style="display: block;">REMARKS</span>
                {{ doc.manpower_remarks }}
            </div>
            {% endif %}
        {% endif %}
    </div>

    {# ==================== PAGE 2: WORK PLAN ==================== #}
    {% set wp_grouped = {} %}
    {# Note: Removed boolean flag has_wp_items due to scoping issues. We use wp_grouped length check instead. #}

    {% for m in doc.milestones %}
        {# Robust Status Check: Lowercase AND Trim #}
        {% set s_lower = (m.status or "") | lower | trim %}
        {% if s_lower == "wip" or s_lower == "not started" %}
            {% set raw_plan = m.work_plan or "" %}

            {# Fix: Check if specific delimiter exists, otherwise try newline split #}
            {% if "$#,,," in raw_plan %}
                {% set plan_parts = raw_plan.split('$#,,,') %}
            {% else %}
                {% set plan_parts = raw_plan.split('\n') %}
            {% endif %}

            {% set valid_parts = [] %}
            {% for part in plan_parts %}
                {% if part|trim|length > 0 %}
                    {% set _ = valid_parts.append(part|trim) %}
                {% endif %}
            {% endfor %}

            {% if valid_parts|length > 0 %}
                {% set h = m.work_header or "General" %}
                {% if h not in wp_grouped %}{% set _ = wp_grouped.update({h: []}) %}{% endif %}
                {% set _ = m.update({'__clean_plan': valid_parts}) %}
                {% set _ = wp_grouped[h].append(m) %}
            {% endif %}
        {% endif %}
    {% endfor %}

    {# FIX: Check dictionary length, not a boolean set inside loop #}
    {% if wp_grouped | length > 0 %}
    <div class="page-break-after">
        {{ header_block() }}
        <div class="mt-15"></div>
        <div class="section-title">WORK PLAN</div>

        {% for header in wp_grouped | sort %}
        <div class="mb-20 avoid-break">
            <h4 class="text-lg font-bold mb-10 mt-10">{{ header }} - {{ "%02d" | format(wp_grouped[header]|length) }}</h4>
            {% for m in wp_grouped[header] %}
            <div class="mb-15">
                <div class="mb-4">
                    <span class="font-medium text-md">{{ m.work_milestone_name }} - </span>
                    <span class="text-md {% if (m.status or '')|lower|trim == 'wip' %}text-orange{% else %}text-red{% endif %}">
                        ({{ m.status }})
                    </span>
                </div>
                <ul class="work-plan-ul">
                    {% for line in m.__clean_plan %}
                    <li class="work-plan-li">{{ line }}</li>
                    {% endfor %}
                </ul>
            </div>
            {% endfor %}
        </div>
        {% endfor %}
    </div>
    {% endif %}

    {# ==================== PAGE 3: WORK PROGRESS ==================== #}
    {% set prog_grouped = {} %}
    {# Note: Removed boolean flag has_prog_items due to scoping issues. We use prog_grouped length check instead. #}

    {% for m in doc.milestones %}
        {% if m.status and m.status != "Not Applicable" %}
            {% set h = m.work_header or "General" %}
            {% if h not in prog_grouped %}{% set _ = prog_grouped.update({h: []}) %}{% endif %}
            {% set _ = prog_grouped[h].append(m) %}
        {% endif %}
    {% endfor %}

    {# FIX: Check dictionary length, not a boolean set inside loop #}
    {% if prog_grouped | length > 0 %}
    <div class="page-break-after">
        {{ header_block() }}
        <div class="mt-15"></div>
        <div class="section-title">WORK PROGRESS</div>

        {% for header in prog_grouped | sort %}
            {% set group_ms = prog_grouped[header] %}
            {% set ns = namespace(total=0) %}
            {% for m in group_ms %}
                {% set ns.total = ns.total + (m.progress | float) %}
            {% endfor %}
            {% set avg_p = (ns.total / (group_ms|length)) | round | int %}

            <div class="avoid-break mt-20">
                <div class="group-header-box">
                    <table class="w-100 progress-circle-group">
                        <tr>
                            <td class="valign-middle">
                                <span class="font-bold text-lg text-black">{{ header }} ({{ group_ms|length }})</span>
                            </td>
                            <td class="text-right valign-middle" style="width: 150px;">
                                <table class="w-100">
                                    <tr>
                                        <td class="text-right font-bold text-sm pr-10">Overall Progress:</td>
                                        <td style="width: 40px;">
                                            <svg width="36" height="36" viewBox="0 0 36 36">
                                                <g transform="rotate(-90 18 18)">
                                                    <circle cx="18" cy="18" r="14" fill="none" stroke="#e5e7eb" stroke-width="4"></circle>
                                                    {% set circ = 2 * 3.14159 * 14 %}
                                                    {% set off = circ * (1 - avg_p/100) %}
                                                    <circle cx="18" cy="18" r="14" fill="none" stroke="#dc2626" stroke-width="4" 
                                                            stroke-dasharray="{{ circ }}" stroke-dashoffset="{{ off }}" stroke-linecap="round"></circle>
                                                </g>
                                            </svg>
                                            <div class="progress-pct-text">{{ avg_p }}%</div>
                                        </td>
                                    </tr>
                               </table>
                            </td>
                        </tr>
                    </table>
                </div>

                <table class="data-table">
                    <thead>
                        <tr>
                            <th class="w-40">WORK</th>
                            <th class="w-20">STATUS</th>
                            <th class="w-20">PROGRESS</th>
                            <th class="w-20">Expected Date</th>
                        </tr>
                    </thead>
                    <tbody>
                        {% for m in group_ms %}
                        <tr>
                            <td class="text-center">
                                <span class="font-medium text-sm">{{ m.work_milestone_name }}</span>
                                {% if m.remarks %}
                                <div class="bg-yellow-vlight remarks-bubble">📂 {{ m.remarks }}</div>
                                {% endif %}
                            </td>
                            <td class="text-center">
                                {% set s_cls = "bg-gray-light" %}
                                {% if m.status == "Completed" %}{% set s_cls = "bg-green-light" %}
                                {% elif m.status == "WIP" %}{% set s_cls = "bg-yellow-light" %}
                                {% elif m.status == "Not Started" %}{% set s_cls = "bg-red-light" %}
                                {% endif %}
                                <span class="badge {{ s_cls }}">{{ m.status }}</span>
                            </td>
                            <td class="text-center">
                                {% set p_val = m.progress | int %}
                                {% set p_col = "#10b981" %}
                                {% if p_val < 50 %}{% set p_col = "#dc2626" %}
                                {% elif p_val < 90 %}{% set p_col = "#d97706" %}
                                {% endif %}
                                <span class="font-bold text-sm" style="color: {{ p_col }};">{{ p_val }}%</span>
                            </td>
                            <td class="text-center">
                                {% if m.status == "Not Started" %}
                                    <span class="text-red font-medium text-sm">
                                        {{ frappe.utils.formatdate(m.expected_starting_date, "dd-MMM-yyyy") if m.expected_starting_date else "N/A" }}
                                    </span>
                                {% else %}
                                    <span class="text-green font-medium text-sm">
                                        {{ frappe.utils.formatdate(m.expected_completion_date, "dd-MMM-yyyy") if m.expected_completion_date else "N/A" }}
                                    </span>
                                {% endif %}
                            </td>
                        </tr>
                        {% endfor %}
                    </tbody>
                </table>
            </div>
        {% endfor %}
    </div>
    {% endif %}

    {# ==================== PAGE 4: IMAGES ==================== #}
    {% set attachment_list = doc.attachments or doc.photos or [] %}
    {% if attachment_list and attachment_list|length > 0 %}
    <div class="page-break-before">
        {{ header_block() }}
        <div class="mt-15"></div>
        <div class="section-title">WORK IMAGES</div>

        <table class="image-table">
            {% for i in range(0, attachment_list|length, 2) %}
            <tr>
                {% set att1 = attachment_list[i] %}
                {% if att1 %}
                <td class="image-cell">
                    {% set image_src = att1.file_url or att1.image_link or att1.image %}
                    {% set final_url = frappe.call("nirmaan_stack.api.frappe_s3_attachment.get_s3_temp_url", file_url=image_src) %}
                    {% if final_url %}
                    <div class="image-container">
                        <img src="{{ final_url }}" class="image-actual" alt="Work Image">
                    </div>
                    <div class="image-meta">
                        <div class="image-location text-red">
                            📍 <span class="text-black">{{ att1.location_name or att1.location or "Site Location" }}</span>
                        </div>
                        <div class="bg-yellow-vlight image-remarks">
                            {{ att1.remarks or "No remarks provided." }}
                        </div>
                    </div>
                    {% endif %}
                </td>
                {% endif %}

                <td style="width: 20px; border: none;"></td>

                {% if i + 1 < attachment_list|length %}
                    {% set att2 = attachment_list[i+1] %}
                    <td class="image-cell">
                        {% set image_src2 = att2.file_url or att2.image_link or att2.image %}
                        {% set final_url2 = frappe.call("nirmaan_stack.api.frappe_s3_attachment.get_s3_temp_url", file_url=image_src2) %}
                        {% if final_url2 %}
                        <div class="image-container">
                            <img src="{{ final_url2 }}" class="image-actual" alt="Work Image">
                        </div>
                        <div class="image-meta">
                            <div class="image-location text-red">
                                📍 <span class="text-black">{{ att2.location_name or att2.location or "Site Location" }}</span>
                            </div>
                            <div class="bg-yellow-vlight image-remarks">
                                {{ att2.remarks or "No remarks provided." }}
                            </div>
                        </div>
                        {% endif %}
                    </td>
                {% else %}
                    <td class="image-cell" style="background: none; border: none;"></td>
                {% endif %}
            </tr>
            <tr><td colspan="3" style="height: 20px; border: none;"></td></tr>
            {% endfor %}
        </table>
    </div>
    {% endif %}

</div>
